  GLADE_HOOKUP_OBJECT (DebuggerSettingsWindow, max_children, "max_children");
  GLADE_HOOKUP_OBJECT (DebuggerSettingsWindow, max_depth, "max_depth");
  GLADE_HOOKUP_OBJECT (DebuggerSettingsWindow, label23, "label23");
